window.addEventListener("load",()=>
{
    $(".loadanimation").fadeOut("slow"),
    $("#tags ul li a").click(function(e){
        e.preventDefault()});
        const navBtn=document.querySelector("#navbtn"),
        navBtnlines=document.querySelectorAll(".navbtn div"),
        nav=document.querySelector("nav"),
        navLinks=document.querySelectorAll("nav a");
        var navShow=0;
        navBtn.addEventListener("click",()=>{
            if(0==navShow){
                nav.style.opacity="1",
                nav.style.transform="translateX(0%)";
                let dl=200;
                for(var i=0;i<navLinks.length;i++)
                navLinks[i].style.opacity=1,
                navLinks[i].style.transform="translateX(0%)",
                navLinks[i].style.transitionDelay=dl+"ms",
                dl+=200;
                navBtnlines[1].style.opacity=0,
                navBtnlines[0].style.top="-5px",
                navBtnlines[0].style.transform="rotateZ(45deg)",
                navBtnlines[2].style.transform="rotateZ(-45deg)",
                navShow=1
            }else{
                nav.style.opacity="0",
                nav.style.transform="translateX(100%)",
                navLinks.forEach(item=>{
                    item.style.transitionDelay="0s",
                    item.style.opacity=0}),
                    navBtnlines[1].style.opacity=1,
                    navBtnlines[0].style.top="0px",
                    navBtnlines[0].style.transform="rotateZ(0deg)",
                    navBtnlines[2].style.transform="rotateZ(0deg)";
                    for(var i=0;i<navLinks.length;i++)
                    navLinks[i].style.transform="translateX(100%)";
                    navShow=0
                }
            }),
            $(window).scroll(function(){
                $(document).scrollTop()>=$(".progressbars")
                .offset().top-window.innerHeight/2&&$(".bar")
                .each(function(){$(this).find(".bar-inner")
                .css("width",$(this).attr("data-width"))});
            });
            var lineDrawing=anime({targets:"#wrapper .lines path",strokeDashoffset:[anime.setDashoffset,0],easing:"easeInOutSine",delay:function(el,i){return 1e3+100*i},duration:function(el,i){return 500+800*i},direction:"alternate",loop:!0})});